import axios from 'axios';

const API_URL = 'http://localhost:8000/api'; // Altere para o seu endpoint real

// Função para obter notícias
export const getNews = async () => {
  const response = await axios.get(`${API_URL}/news`);
  return response.data;
};

// Função para criar uma nova notícia
export const createNews = async (newsData) => {
  const response = await axios.post(`${API_URL}/news`, newsData);
  return response.data;
};

// Função para atualizar uma notícia
export const updateNews = async (newsId, newsData) => {
  const response = await axios.put(`${API_URL}/news/${newsId}`, newsData);
  return response.data;
};

// Função para excluir uma notícia
export const deleteNews = async (newsId) => {
  const response = await axios.delete(`${API_URL}/news/${newsId}`);
  return response.data;
};

// Função de login
export const loginUser = async (credentials) => {
  const response = await axios.post(`${API_URL}/login`, credentials);
  return response.data;
};
