// src/components/AdminPanel.js
import React, { useEffect, useState } from 'react';
import { getNews } from '../../services/api';
import './AdminPanel.css'; // Adicione um arquivo CSS para estilização se necessário

const AdminPanel = () => {
    const [news, setNews] = useState([]);
    const [error, setError] = useState('');
    const [loading, setLoading] = useState(true); // Estado para carregamento

    const fetchNews = async () => {
        setLoading(true); // Começa o carregamento
        try {
            const data = await getNews();
            setNews(data);
        } catch (err) {
            setError('Falha ao buscar notícias');
        } finally {
            setLoading(false); // Finaliza o carregamento
        }
    };

    useEffect(() => {
        fetchNews();
    }, []);

    return (
        <div className="admin-panel">
            <h2>Painel de Administração</h2>
            {loading && <p>Carregando...</p>} {/* Mensagem de carregamento */}
            {error && <p className="error-message">{error}</p>} {/* Mensagem de erro */}
            <ul>
                {news.map((item) => (
                    <li key={item.id} className="news-item">
                        <h3>{item.title}</h3>
                        <p>{item.summary}</p> {/* Exibir um resumo se disponível */}
                        <span>{new Date(item.date).toLocaleDateString()}</span> {/* Exibir data formatada */}
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default AdminPanel;
