import axios from 'axios';

const api = axios.create({
  baseURL: 'http://127.0.0.1:8000/api',
});

// Authentication
export const registerUser = async (data) => api.post('/register', data);
export const loginUser = async (data) => api.post('/login', data);
export const logoutUser = async () => api.post('/logout');
export const getUser = async () => api.get('/user');

// News
export const getNews = async () => api.get('/news');
export const getNewsDetail = async (id) => api.get(`/news/${id}`);
export const createNews = async (data) => api.post('/news', data);
export const updateNews = async (id, data) => api.put(`/news/${id}`, data);
export const deleteNews = async (id) => api.delete(`/news/${id}`);
