import React, { useContext } from 'react';
import { UserContext } from '../context/UserContext'; // Ajuste o caminho conforme necessário

const Dashboard = () => {
    const { user } = useContext(UserContext); // Obtenha o contexto do usuário

    // Verifique se o usuário está definido antes de acessar suas propriedades
    if (!user) {
        return <div>Carregando...</div>; // Ou redirecione para login
    }

    return (
        <div className="dashboard">
            <h1>Painel de Controle</h1>
            <p>Bem-vindo, {user.name}!</p>
            {user.userType === 'admin' && (
                <p>Você tem acesso completo ao sistema.</p>
            )}
            {user.userType === 'editor' && (
                <p>Você pode criar e editar notícias.</p>
            )}
            {user.userType === 'reader' && (
                <p>Você pode visualizar notícias.</p>
            )}
        </div>
    );
};

export default Dashboard;
