// src/components/CreateNews.js
import React, { useState } from 'react';
import { createNews } from '../../services/api';
import './CreateNews.css';

const CreateNews = () => {
    const [title, setTitle] = useState('');
    const [subtitle, setSubtitle] = useState(''); // Novo estado para o subtítulo
    const [content, setContent] = useState('');
    const [image, setImage] = useState(''); // Novo estado para a imagem

    const handleCreateNews = async (e) => {
        e.preventDefault();
        try {
            await createNews({ title, subtitle, content, image }); // Inclua o subtítulo e a imagem
            window.location.href = '/news';
        } catch (error) {
            console.error("Error creating news:", error);
        }
    };

    return (
        <div className="create-news-container">
            <h1>Create News</h1>
            <form onSubmit={handleCreateNews}>
                <input
                    type="text"
                    placeholder="Title"
                    value={title}
                    onChange={(e) => setTitle(e.target.value)}
                    required
                />
                <input
                    type="text"
                    placeholder="Subtitle" // Novo campo para o subtítulo
                    value={subtitle}
                    onChange={(e) => setSubtitle(e.target.value)}
                    required
                />
                <textarea
                    placeholder="Content"
                    value={content}
                    onChange={(e) => setContent(e.target.value)}
                    required
                />
                <input
                    type="text"
                    placeholder="Image URL" // Novo campo para a imagem
                    value={image}
                    onChange={(e) => setImage(e.target.value)}
                />
                <button type="submit">Create</button>
            </form>
        </div>
    );
};

export default CreateNews;
