// src/components/AdminPanel.js
import React from 'react';
import { createNews, updateNews, deleteNews } from '../services/api';

function AdminPainel() {
  const handleCreate = async (newsData) => {
    await createNews(newsData);
    alert("Notícia criada com sucesso!");
  };

  const handleUpdate = async (newsId, newsData) => {
    await updateNews(newsId, newsData);
    alert("Notícia atualizada com sucesso!");
  };

  const handleDelete = async (newsId) => {
    await deleteNews(newsId);
    alert("Notícia excluída com sucesso!");
  };

  return (
    <div>
      <h2>Painel Administrativo</h2>
      <button onClick={() => handleCreate({ title: "Nova Notícia", content: "Conteúdo" })}>Criar Notícia</button>
      {/* Colocar inputs para manipular `newsId` e `newsData` no update e delete */}
    </div>
  );
}

export default AdminPainel;
