// src/components/AdminPanel.js
import React, { useState, useEffect } from 'react';
import { getNews, createNews, updateNews, deleteNews } from '../services/api';

const AdminPanel = () => {
    const [news, setNews] = useState([]);
    const [title, setTitle] = useState('');
    const [content, setContent] = useState('');
    const [editMode, setEditMode] = useState(false);
    const [currentNewsId, setCurrentNewsId] = useState(null);

    const fetchNews = async () => {
        const result = await getNews();
        setNews(result);
    };

    useEffect(() => {
        fetchNews();
    }, []);

    const handleCreate = async () => {
        await createNews({ title, content });
        fetchNews();
        setTitle('');
        setContent('');
    };

    const handleEdit = (newsItem) => {
        setTitle(newsItem.title);
        setContent(newsItem.content);
        setEditMode(true);
        setCurrentNewsId(newsItem.id);
    };

    const handleUpdate = async () => {
        await updateNews(currentNewsId, { title, content });
        fetchNews();
        setTitle('');
        setContent('');
        setEditMode(false);
    };

    const handleDelete = async (id) => {
        await deleteNews(id);
        fetchNews();
    };

    return (
        <div>
            <h1>Admin Panel</h1>
            <input
                type="text"
                placeholder="Title"
                value={title}
                onChange={(e) => setTitle(e.target.value)}
            />
            <textarea
                placeholder="Content"
                value={content}
                onChange={(e) => setContent(e.target.value)}
            />
            {editMode ? (
                <button onClick={handleUpdate}>Update News</button>
            ) : (
                <button onClick={handleCreate}>Create News</button>
            )}
            <ul>
                {news.map((newsItem) => (
                    <li key={newsItem.id}>
                        <h3>{newsItem.title}</h3>
                        <p>{newsItem.content}</p>
                        <button onClick={() => handleEdit(newsItem)}>Edit</button>
                        <button onClick={() => handleDelete(newsItem.id)}>Delete</button>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default AdminPanel;
