// src/components/EditNews.js
import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { getNewsById, updateNews } from '../../services/api';

const EditNews = () => {
    const { id } = useParams();
    const [newsData, setNewsData] = useState({ title: '', subtitle: '', content: '', image: '' });

    useEffect(() => {
        const fetchNews = async () => {
            try {
                const news = await getNewsById(id);
                setNewsData(news);
            } catch (error) {
                console.error("Failed to fetch news", error);
            }
        };
        fetchNews();
    }, [id]);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setNewsData({ ...newsData, [name]: value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            await updateNews(id, newsData);
            window.location.href = '/news'; // Redirecionar após editar
        } catch (error) {
            console.error("Failed to update news", error);
        }
    };

    return (
        <div>
            <h1>Editar Notícia</h1>
            <form onSubmit={handleSubmit}>
                <input type="text" name="title" placeholder="Título" value={newsData.title} onChange={handleChange} required />
                <input type="text" name="subtitle" placeholder="Subtítulo" value={newsData.subtitle} onChange={handleChange} required />
                <textarea name="content" placeholder="Conteúdo" value={newsData.content} onChange={handleChange} required />
                <input type="text" name="image" placeholder="URL da Imagem" value={newsData.image} onChange={handleChange} />
                <button type="submit">Atualizar</button>
            </form>
        </div>
    );
};

export default EditNews;
