import React, { useEffect, useState, useContext } from 'react';
import { getNews, createNews, deleteNews } from '../services/api';
import { AuthContext } from '../context/AuthContext';
import NewsForm from '../components/NewsForm';
import NewsList from '../components/NewsList';
import '../styles/Dashboard.css';

const Dashboard = () => {
    const { user } = useContext(AuthContext);
    const [news, setNews] = useState([]);
    const [editing, setEditing] = useState(null);

    useEffect(() => {
        const fetchNews = async () => {
            const data = await getNews();
            setNews(data);
        };
        fetchNews();
    }, []);

    const handleDelete = async (id) => {
        await deleteNews(id);
        setNews(news.filter((item) => item.id !== id));
    };

    return (
        <div className="dashboard">
            <h1>Painel de Administração</h1>
            {user.userType === 'Admin' || user.userType === 'Editor' ? (
                <NewsForm setNews={setNews} editing={editing} setEditing={setEditing} />
            ) : null}
            <h2>Lista de Notícias</h2>
            <NewsList news={news} handleDelete={handleDelete} setEditing={setEditing} />
        </div>
    );
};

export default Dashboard;
