import React, { createContext, useState, useEffect } from 'react';
import { loginUser } from '../services/api'; // Importar a função de login

export const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
    const [user, setUser] = useState(null);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const token = localStorage.getItem('token');
        const userType = localStorage.getItem('userType');
        if (token && userType) {
            setUser({ token, userType });
        }
        setLoading(false);
    }, []);

    // Função de login que será usada no Login.js
    const login = async (credentials) => {
        const data = await loginUser(credentials); // Chama a API de login
        console.log(data);
        localStorage.setItem('token', data.token);
        localStorage.setItem('userType', data.userType);
        setUser({ token: data.token, userType: data.userType }); // Define o usuário no contexto
    };

    const logout = () => {
        localStorage.removeItem('token');
        localStorage.removeItem('userType');
        setUser(null);
    };

    return (
        <AuthContext.Provider value={{ user, login, logout, loading }}>
            {children}
        </AuthContext.Provider>
    );
};
