// src/services/api.js
import axios from 'axios';

const API_URL = 'http://localhost:8000/api'; // Ajuste para a URL do seu backend

export const getNews = async () => {
  const token = localStorage.getItem('token'); // ou de onde você armazenar o token
  const response = await axios.get(`${API_URL}/news`, {
    headers: {
        Authorization: `Bearer ${token}` // Enviando o token no cabeçalho
    }
});
    return response.data;
};

export const createNews = async (data) => {
    const response = await axios.post(`${API_URL}/news`, data);
    return response.data;
};

export const updateNews = async (id, data) => {
    const response = await axios.put(`${API_URL}/news/${id}`, data);
    return response.data;
};

export const deleteNews = async (id) => {
    await axios.delete(`${API_URL}/news/${id}`);
};

export const loginUser = async (data) => {
    const response = await axios.post(`${API_URL}/login`, data);
    return response.data; // Guarde o token se necessário
};

export const registerUser = async (data) => {
    const response = await axios.post(`${API_URL}/register`, data);
    return response.data;
};
