// src/components/CreateNews.js
import React, { useState } from 'react';
import { useHistory } from 'react-router-dom'; // Importe useHistory para navegação
import { createNews } from '../../services/api';
import './CreateNews.css';

const CreateNews = () => {
    const [title, setTitle] = useState('');
    const [subtitle, setSubtitle] = useState(''); // Adicione o estado para o subtítulo
    const [content, setContent] = useState('');
    const history = useHistory(); // Inicializa useHistory

    const handleCreateNews = async (e) => {
        e.preventDefault();
        try {
            await createNews({ title, subtitle, content }); // Inclua o subtítulo ao criar a notícia
            history.push('/news'); // Redireciona para a lista de notícias
        } catch (error) {
            console.error("Error creating news:", error);
        }
    };

    const handleGoBack = () => {
        history.push('/news'); // Função para voltar à lista de notícias
    };

    return (
        <div className="create-news-container">
            <h1>Create News</h1>
            <form onSubmit={handleCreateNews}>
                <input
                    type="text"
                    placeholder="Title"
                    value={title}
                    onChange={(e) => setTitle(e.target.value)}
                    required
                />
                <input
                    type="text"
                    placeholder="Subtitle" // Adicione o campo de subtítulo
                    value={subtitle}
                    onChange={(e) => setSubtitle(e.target.value)}
                    required
                />
                <textarea
                    placeholder="Content"
                    value={content}
                    onChange={(e) => setContent(e.target.value)}
                    required
                />
                <button type="submit">Create</button>
            </form>
            <button onClick={handleGoBack} className="back-button">Voltar para a lista de notícias</button> {/* Botão de voltar */}
        </div>
    );
};

export default CreateNews;
