// src/components/CreateNews.js
import React, { useState } from 'react';
import { createNews } from '../../services/api';
import  './CreateNews.css';
const CreateNews = () => {
    const [newsData, setNewsData] = useState({ title: '', subtitle: '', content: '', image: '' });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setNewsData({ ...newsData, [name]: value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            await createNews(newsData);
            window.location.href = '/news'; // Redirecionar após criar
        } catch (error) {
            console.error("Failed to create news", error);
        }
    };

    return (
        <div>
            <h1>Criar Nova Notícia</h1>
            <form onSubmit={handleSubmit}>
                <input type="text" name="title" placeholder="Título" value={newsData.title} onChange={handleChange} required />
                <input type="text" name="subtitle" placeholder="Subtítulo" value={newsData.subtitle} onChange={handleChange} required />
                <textarea name="content" placeholder="Conteúdo" value={newsData.content} onChange={handleChange} required />
                <input type="text" name="image" placeholder="URL da Imagem" value={newsData.image} onChange={handleChange} />
                <button type="submit">Criar</button>
            </form>
        </div>
    );
};

export default CreateNews;
